interface IZirconPylonRouter {
    function factory() external pure returns (address);
    function WETH() external pure returns (address);
    function pylonFactory() external pure returns (address);

    function init(
        address tokenA,
        address tokenB,
        uint amountDesiredA,
        uint amountDesiredB,
        address to,
        uint deadline
    ) external returns (uint amountA, uint amountB);

    function initETH(
        address token,
        uint amountDesiredToken,
        bool isAnchor,
        address to,
        uint deadline
    ) external payable returns (uint amountA, uint amountB);

    function addSyncLiquidity(
        address tokenA,
        address tokenB,
        uint amountDesired,
        bool isAnchor,
        address to,
        uint deadline
    ) external returns (uint amount, uint liquidity);

    function addSyncLiquidityETH(
        address token,
        bool isAnchor,
        address to,
        uint deadline
    ) external payable returns (uint liquidity);

    function addAsyncLiquidity100(
        address tokenA,
        address tokenB,
        uint amountDesired,
        bool isAnchor,
        address to,
        uint deadline
    ) external returns (uint liquidity);

    function addAsyncLiquidity100ETH(
        address token,
        bool isAnchor,
        address to,
        uint deadline
    ) external payable returns (uint liquidity);

    function addAsyncLiquidity(
        address tokenA,
        address tokenB,
        uint amountADesired,
        uint amountBDesired,
        uint amountAMin,
        uint amountBMin,
        bool shouldReceiveAnchor,
        address to,
        uint deadline
    ) external returns (uint amountA, uint amountB, uint liquidity);

    function addAsyncLiquidityETH(
        address token,
        uint amountDesiredToken,
        uint amountTokenMin,
        uint amountETHMin,
        bool isAnchor,
        bool shouldReceiveAnchor,
        address to,
        uint deadline
    )  external payable returns (uint amountA, uint amountB, uint liquidity);

    function removeLiquiditySync(
        address tokenA,
        address tokenB,
        uint liquidity,
        uint amountMin,
        bool shouldReceiveAnchor,
        address to,
        uint deadline
    ) external returns (uint amount);
    function removeLiquiditySyncETH(
